package org.home.hackerrank.hackfest2020;

//https://www.hackerrank.com/contests/hackerrank-hackfest-2020/challenges/cyclic-binary-string
public class CyclicBinaryString {

    public static int maximumPower(String s) {
        // Write your code here
        int result = 0;
        int current = 0;
        boolean found = false;

        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);

            if (c == '1') {
                current = 0;
                found = true;
            } else {
                current += 1;
            }

            if (current > result) {
                result = current;
            }
        }

        if (!found) {
            return -1;
        }

        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);

            if (c == '1') {
                break;
            } else {
                current += 1;
            }

            if (current > result) {
                result = current;
            }
        }

        return result;
    }

    public static void main(String[] args) {
        System.out.println(maximumPower("0100"));
        System.out.println(maximumPower("0010"));
        System.out.println(maximumPower(""));
    }
}
